mapper.insert:
        INSERT INTO
            "USERS_ROLES" ("USERS_id", "ROLES_id")
        VALUES (
                (
                    SELECT
                        id
                    FROM
                        "USERS"
                    WHERE
                        username = #{username}
                ),
                (
                    SELECT
	                    id
                    FROM
	                    "ROLES"
                    WHERE
	                    "name" = #{roleName}
                )
        );
